import React, {useState, useRef, useEffect} from "react"
import Game from "./Game"
import GameGenre from "./GameGenre";
import "./css/dropdown.css"


function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);

  useEffect(()=>{
    getGamesGenre()
  },[])

  const changeSelected = event => {
    let nes = event.target.selectedIndex;
    console.log(gamesGenre[nes]);
    console.log(nes);
    setGames(gamesGenre[nes].name)

  }
  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();
    console.log(data)
    setGamesGenre(data)
  }

  return (
    <div>
      <input type="text" placeholder="Search..."></input>
      <div>
        <div class="dropdown">
          <p>Å½anr igara</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
                gamesGenre.map(genre => {
                  return <Game genre={genre.genre} />
              })

            }
          </select>
        </div>

        <div class="dropdown">
        <p>Igre</p>
            <select class="dropdown-content">
              {games.map(game =>(
                <Game genre={game}/>))}
            </select>
        </div>
      </div>
    </div>
  );
}

export default App;
