import React, { useState, useEffect } from "react"
import "./css/dropdown.css";
import { BrowserRouter, Switch, Route } from 'react-router-dom';
import ItemDetail from "./ItemDetail";
import Header from "./Header";
import Cart from "./Cart";
import Products from "./Products";
import { CartProvider } from "./CartContext";





function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [cart, setCart] = useState([]);

  const [isOpen, setIsOpen] = useState(false);
  const [tempGame, setTempGame] = useState();//modal
  const [counter, setCounter] = useState(1)//brojac


  useEffect(() => { //za dropdown
    getGamesGenre()
  }, [])

  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();

    setGamesGenre(data)
  }

  return (
    <BrowserRouter >
      <CartProvider>
        <div>
          <header>
            <Header/>
          </header>
          {
            <Switch>
              <Route path="/" exact component={Products} />
              <Route path="/cart" component={Cart} />
              <Route path="/:id" component={ItemDetail} />
            </Switch>
          }
        </div>
      </CartProvider>
    </BrowserRouter>
  );
}

export default App;
