import React, {useState, useRef, useEffect} from "react"
import Game from "./Game"
import GameGenre from "./GameGenre";
import "./css/dropdown.css"
import ListGames from "./ListGames";

const PAGE_PRODUCT = 'products';
const PAGE_CART = 'cart';


function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [cart,setCart] =useState([]);
  const [page,setPage] = useState(PAGE_PRODUCT)

  useEffect(()=>{ //za dropdown
    getGamesGenre()
  },[])

  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();

    setGamesGenre(data)
  }
  const changeSelected = event => {
    let nes = event.target.selectedIndex;
    //console.log(gamesGenre[nes]);
    setGames(gamesGenre[nes].name)

  }

  const addToCart = (product) =>{
    setCart([...cart,product]);
  };
  const removeFromCart = (productToRemove) =>{
    setCart(cart.filter((product) => product !==productToRemove))
  };

  const navigateTo = (nextPage) =>{
    setPage(nextPage);
  }

  const renderProducts = () => (
    <>
      <div>
        <div class="dropdown">
          <p>Å½anr igara</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre={gamesGenre} />
            }
          </select>
        </div>
      </div>

      <div class="search">
        <input class="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          games.filter((game => {
            if (searchTerm == "") {
              return game //nefiltrira nista ako je prazno i samo mapira
            } else if (game.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <p class="search-list"><ListGames game={game} onClick={addToCart} /></p>))
        }

      </div>
    </>
  );

  const renderCart = () => (
    <>
      <div>
        <div class="dropdown">
          <p>CART</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre={gamesGenre} />
            }
          </select>
        </div>
      </div>

      <div class="search">
        <input class="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          cart.filter((game => {
            if (searchTerm == "") {
              return game //nefiltrira nista ako je prazno i samo mapira
            } else if (game.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <div>
              <p class="search-list"><ListGames game={game} onClick={removeFromCart} /> </p>
              </div>))
        }

      </div>
    </>
  );

  return (
    <div>
      <header>
       <button onClick={() => navigateTo(PAGE_PRODUCT)} class="header-button">View Products</button>
        <button onClick={() => navigateTo(PAGE_CART)} class="header-button">Go to Cart({cart.length})</button>
      </header>
      <>
      {page === PAGE_PRODUCT && renderProducts()}
      {page === PAGE_CART && renderCart()}
      </>

    </div>
  );
}

export default App;
