import React, {useState, useRef, useEffect} from "react"
import Game from "./Game"
import GameGenre from "./GameGenre";
import "./css/dropdown.css"
import ListGames from "./ListGames";



function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [cart,setCart] =useState([]);

  useEffect(()=>{ //za dropdown
    getGamesGenre()
  },[])


  const changeSelected = event => {
    let nes = event.target.selectedIndex;
    //console.log(gamesGenre[nes]);
    setGames(gamesGenre[nes].name)

  }
  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();

    setGamesGenre(data)
  }
  const addToCart = (product) =>{
    console.log("delete",product)
    setCart([...cart,product]);
  };

  return (
    <div>
      <header>
        <button>Go to Cart({cart.length})</button>
      </header>
      <div>
        <div class="dropdown">
          <p>Å½anr igara</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre ={gamesGenre}/>
            }
          </select>
        </div>


      </div>

      <div class="search">
        <input class="search-input" type="text" placeholder="Search..." onChange={event => {setSearchTerm(event.target.value)}}></input>
        {
          games.filter((game=>{
            if (searchTerm ==""){
              return game //nefiltrira nista ako je prazno i samo mapira
            } else if (game.toLowerCase().includes(searchTerm.toLowerCase())){
              return game //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <p class="search-list"><ListGames game={game} onClick={addToCart}/></p>))
        }

      </div>
    </div>
  );
}

export default App;
