import React, {useState, useRef, useEffect} from "react"
import Game from "./Game"
import GameGenre from "./GameGenre";
import "./css/dropdown.css"
import ListGames from "./ListGames";
import {MdShoppingCart} from 'react-icons/md'
import {MdRemoveShoppingCart} from 'react-icons/md'


const PAGE_PRODUCT = 'products';
const PAGE_CART = 'cart';


function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [cart,setCart] =useState([]);
  const [page,setPage] = useState(PAGE_PRODUCT)

  useEffect(()=>{ //za dropdown
    getGamesGenre()
  },[])

  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();

    setGamesGenre(data)
  }
  const changeSelected = event => {
    let nes = event.target.selectedIndex;
    //console.log(gamesGenre[nes]);
    setGames(gamesGenre[nes].product)

  }

  const addToCart = (game) =>{
    setCart([...cart,game]);
  };
  const removeFromCart = (productToRemove) =>{
    //console.log("micem", productToRemove)
    setCart(cart.filter((game) => game !==productToRemove))
  };

  const navigateTo = (nextPage) =>{
    setPage(nextPage);
  }

  const renderProducts = () => (
    <>
      <div>
        <div class="dropdown">
          <p>Žanr igara</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre={gamesGenre} />
            }
          </select>
        </div>
      </div>

      <div class="search">
        <input class="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          games.filter((game => {
            if (searchTerm == "") {
              return game.name //nefiltrira nista ako je prazno i samo mapira
            } else if (game.name.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game.name //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <div class="list">
              <span class="search-list">{game.name}</span>
              <span class="search-list">{game.price}</span>
              <span>
                <MdShoppingCart
                  style={{ cursor: "pointer" }}
                  onClick={()=>addToCart(game)}/>
              </span>
            </div>))
        }

      </div>
    </>
  );

  const renderCart = () => (
    <>


      <div class="search">
      <p>Shopping Cart</p>
        <input class="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          cart.filter((game => {
            if (searchTerm == "") {
              return game.name //nefiltrira nista ako je prazno i samo mapira
            } else if (game.name.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game.name //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <div class="list">
              <span class="search-list"><ListGames game={game.name} /> </span>
              <span>
                <MdRemoveShoppingCart
                    style={{ cursor: "pointer" }} onClick={()=>removeFromCart(game)} />
                </span>
              </div>))
        }

      </div>
    </>
  );

  return (
    <div>
      <header>
       <button onClick={() => navigateTo(PAGE_PRODUCT)} class="header-button">View Products</button>
        <button onClick={() => navigateTo(PAGE_CART)} class="header-button">Go to Cart({cart.length})</button>
      </header>
      <>
      {page === PAGE_PRODUCT && renderProducts()}
      {page === PAGE_CART && renderCart()}
      </>

    </div>
  );
}

export default App;
