import React, {useState, useRef, useEffect} from 'react'
import Game from './Game'
import './css/dropdown.css'


function App() {

  const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);

  useEffect(()=>{
    getGamesGenre()
  },[])

  const changeSelected = event => {
    let nes = event.target.selectIndex;
    setGames(games[nes].name)
  }

  const getGamesGenre = async () => {
    const response = await fetch('http://demo2580688.mockable.io/Games')
    const data = await response.json();
    setGamesGenre(data)
  }

  return (
    <div >
      <button class="dropbtn">Stisni</button>
      <div>

        <div >
          <select class="dropdown-content" onChange={changeSelected}>
            {gamesGenre.map(gameGenre => (
              <Game gameGenre={gameGenre.type} />

            ))}
          </select>

        </div>
        <div>
            <select class="dropdown-content">
              {games.map(game =>(
                <Game gameGenre={game}/>))}
            </select>
        </div>
      </div>
    </div>
  );
}

export default App;
