import React, {useState, useEffect} from "react"
import GameGenre from "./GameGenre";
import "./css/dropdown.css";
import {MdRemoveShoppingCart, MdShoppingCart} from 'react-icons/md';
import Modal from "./Modal";
import {BrowserRouter , Switch,Route,Link} from 'react-router-dom';
import ItemDetail from "./ItemDetail"; 
import Cart from "./Cart";


const PAGE_PRODUCT = 'products';
const PAGE_CART = 'cart';
export default function Functions() {

    const [gamesGenre, setGamesGenre] = useState([]);
  const [games, setGames] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [cart,setCart] =useState([]);
  const [page,setPage] = useState(PAGE_PRODUCT);//izbrisat kad osposobim rout
  const [isOpen, setIsOpen] = useState(false);
  const [tempGame,setTempGame] = useState();//modal
  const [counter,setCounter] = useState(1)//brojac
  
  useEffect(()=>{ //za dropdown
    getGamesGenre()
  },[])

  const getGamesGenre = async () => {
    const response = await fetch("http://demo2580688.mockable.io/Games")
    const data = await response.json();
    
    setGamesGenre(data)
  }
  const changeSelected = event => {
    let nes = event.target.selectedIndex;
    console.log(nes);
    setGames(gamesGenre[nes].product)

  }
  const addToCartBtn = (game) => {
    
    setCart([...cart,{...game}]);//radimo novi objek od game inace 
    //se duplicira i onda kada brisemo brisemo sve
    setIsOpen(false)
  };

  const addToCart = (game) =>{
    setIsOpen(true)
    setTempGame(game) //pamtimo da mozemo u modelu dodat
    
  };
  const removeFromCart = (productToRemove) =>{
    //console.log("micem", productToRemove)
    setCart(cart.filter((game) => game !==productToRemove))
  };

  const navigateTo = (nextPage) =>{
    setPage(nextPage);
  }

  function increase(){
    setCounter(counter +1)
  }
  function decrease(){
    setCounter(counter-1)
    /*if (counter == 0 )
    removeFromCart(productToRemove)*/
  }

  const renderProducts = () => (
    <>
      <div>
        <div className="dropdown">
          <p>Žanr igara</p>
          <select className="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre={gamesGenre} />
            }
          </select>
        </div>
      </div>

      <div className="search">
        <input className="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          games.filter((game => {
            if (searchTerm == "") {
              return game.name //nefiltrira nista ako je prazno i samo mapira
            } else if (game.name.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game.name //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <div key= {game.id} className="list">
              <span className="search-list"><Link to={"/${game.id}"}>{game.name}</Link></span>
              <span className="search-list">{game.price}</span>
              <span>
                <MdShoppingCart
                  style={{ cursor: "pointer" }}
                  onClick={()=>addToCart(game)}/>
              </span>
              
            </div>))
        }
      </div>
    </>
  );

  const renderCart = () => (
    <>
      <div className="search">
        <p>Shopping Cart</p>
        <input className="search-input" type="text" placeholder="Search..." onChange={event => { setSearchTerm(event.target.value) }}></input>
        {
          cart.filter((game => {
            if (searchTerm == "") {
              return game.name //nefiltrira nista ako je prazno i samo mapira
            } else if (game.name.toLowerCase().includes(searchTerm.toLowerCase())) {
              return game.name //samo ce vratit one koje includa sto je u inputu
            }
          })).map(game => (
            <div key={game.id} className="list">
              <span className="search-list">{game.name}</span>

              <span className="search-list">{game.price}</span>
              <span>
                <MdRemoveShoppingCart
                  style={{ cursor: "pointer" }} onClick={() => removeFromCart(game)} />
              </span>
              <button onClick={increase} className="list-counter">+</button>
              <h2 className="list-counter-text">{counter}</h2>
              <button onClick={decrease} className="list-counter">-</button>
              
            </div>))
        }

      </div>
    </>
  );

  const renderDetails = ()=>(
    <div>
     <h1></h1>
    </div>
  );
    return (
        <div>


        <Modal open={isOpen} onClose={()=>setIsOpen(false)} addToCart={()=>addToCartBtn(tempGame)}>
        Želite li dodati u košaricu
        </Modal>
        </div>
    )
}
