      </header>
      {page==='products' &&(<>)
      <div>
        <div class="dropdown">
          <p>Å½anr igara</p>
          <select class="dropdown-content" onChange={changeSelected}>
            {
              <GameGenre gamesGenre ={gamesGenre}/>
            }
          </select>
        </div>
      </div>

      <div class="search">
        <input class="search-input" type="text" placeholder="Search..." onChange={event => {setSearchTerm(event.target.value)}}></input>
        {
          games.filter((game=>{
            if (searchTerm ==""){
              return game //nefiltrira nista ako je prazno i samo mapira
